import './App.scss';
import Header from "./components/header/header";
import Home from "./components/home-block/home";
import {useEffect, useState} from "react";
import Authorization from "./components/post-block/authorization";
import Users from "./components/get-block/users";

function App() {

    let [pageNum, setPageNum] = useState(1);
    let [users, setUsers] = useState([]);
    let [loader, setLoader] = useState(false);

    const sliceText = (str) => {
        if (str.length > 20) return str.slice(0, 20) + '...';
        return str;
    };

    useEffect(() => {
        fetch('https://frontend-test-assignment-api.abz.agency/api/v1/token')
            .then(response => response.json())
            .then(data => localStorage.setItem('token', data.token))
    });

    return (
        <div className="wrapper">
            <Header/>
            <Home/>
            <Users
                pageNum={pageNum}
                setPageNum={setPageNum}
                users={users}
                setUsers={setUsers}
                sliceText={sliceText}
                loader={loader}
                setLoader={setLoader}
            />
            <Authorization
                setUsers={setUsers}
                setPageNum={setPageNum}
                loader={loader}
                setLoader={setLoader}
                sliceText={sliceText}
            />
        </div>
    );
}

export default App;
