import React, {useEffect, useState} from 'react';
import './../../App.scss'
import './Get.scss'
import MyButton from "../UI/MyButton/MyButton";

const Get = () => {
    let [pageNum, setPageNum] = useState(1)
    let [users, setUsers] = useState([])
    let [showBtn, setShowBtn] = useState(true)

    const getUsers = async (pageNum) => {
       let response = await fetch(`https://frontend-test-assignment-api.abz.agency/api/v1/users?page=${pageNum}&count=6`)
            return await response.json()
    }

    useEffect(() => {
        getUsers(pageNum)
            .then(data => {
                setUsers(users.concat(data.users))
                setPageNum((prev) => prev++)
            })
    }, [])


    return (
        <section className='get__section'>
            <h1 className='get__title title'> Working with GET request</h1>
            <div className='users__cards'>
                {users.map((user) => {
                    return <div className='user__card'>
                        <div className='body__card'>
                          <img className='avatar' src={user.photo}/>
                            <div className='name'>{user.name}</div>
                            <div className='position'>{user.position}</div>
                            <div className='email'>{user.email}</div>
                            <div className='phone'>{user.phone}</div>
                        </div>
                    </div>
                })}
            </div>
            <button onClick={() => {
                getUsers(pageNum)
                    .then(data => {
                        if(data.data.total_pages === pageNum) setShowBtn(false)
                        setUsers(users.concat(data.users))
                        setPageNum((prev) => prev++)
                    })}}>
                Show more
            </button>
        </section>
    );
};

export default Get;
