import React, {useEffect, useRef, useState} from 'react';
import './../../App.scss'
import './Post.scss'
import {useForm} from "react-hook-form";
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';






const Post = () => {
    const {register, formState: {errors, isValid}, reset, handleSubmit} = useForm({mode: 'all'})
    let [positions, setPosition] = useState([])
    let token = localStorage.getItem('token')

    useEffect( () => {
      fetch('https://frontend-test-assignment-api.abz.agency/api/v1/positions')
            .then(response => response.json())
            .then(data => setPosition(data.positions))
    }, [])

    const setUser = async (data) => {
        const formData = new FormData()
        formData.append('name', data.name)
        formData.append('phone', data.phone)
        formData.append('position_id', data.position_id)
        formData.append('email', data.email)
        formData.append('photo', data.photo[0])
        await fetch(`https://frontend-test-assignment-api.abz.agency/api/v1/users`, {
            method: 'POST',
            body: formData,
            headers: {
                'Token': token
            },
        })
            .then((response) => response)
            .then(data => console.log(data))
    }

    return (
        <section className='post__section'>
            <div className='container'>
                <h1 className='post__title title'>Working with POST request</h1>
                <Box
                    component="form"
                    sx={{
                        '& .MuiTextField-root': { m: 1, width: '25ch' },
                    }}
                    noValidate
                    autoComplete="off"
                >
                    <div>
                        <TextField
                            {...register('name', {
                                required: 'The string must not be empty',
                                minLength: {
                                    value: 2,
                                    message: 'MinLength 2 elements'
                                },
                                maxLength: {
                                    value: 60,
                                    message: 'MaxLength 60 elements'
                                }})}
                            name='name'
                            className='input__validation'
                            type='name'
                            placeholder='Your name'
                            label="Name"
                            defaultValue="Hello World"
                            helperText={errors?.name && errors?.name?.message}
                        />
                    </div>
                    <div>

                    </div>
                </Box>
                <form className='post__form' onSubmit={handleSubmit(setUser)}>
                    <div className='error'>  {errors?.name && <p className='not__valid__input'>{errors?.name?.message}</p>}</div>
                    <input
                        {...register('name', {
                            required: 'The string must not be empty',
                            minLength: {
                                value: 2,
                                message: 'MinLength 2 elements'
                            },
                            maxLength: {
                                value: 60,
                                message: 'MaxLength 60 elements'
                            }})}
                        name='name'
                        className='input__validation'
                        type='name'
                        placeholder='Your name'
                    />
                    <div className='error'>{errors?.email && <p className='not__valid__input'>{errors?.email?.message}</p>}</div>
                    <input
                        {...register('email', {
                            required: 'The string must not be empty',
                            minLength: {
                                value: 2,
                                message: 'MinLength 2 elements'
                            },
                            maxLength: {
                                value: 100,
                                message: 'MaxLength 100 elements'
                            },
                            pattern: {
                                value: /^(?:[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*|"(?:[\x01-\x08\x0b\x0c\x0e-\x1f\x21\x23-\x5b\x5d-\x7f]|\\[\x01-\x09\x0b\x0c\x0e-\x7f])*")@(?:(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?|\[(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?|[a-z0-9-]*[a-z0-9]:(?:[\x01-\x08\x0b\x0c\x0e-\x1f\x21-\x5a\x53-\x7f]|\\[\x01-\x09\x0b\x0c\x0e-\x7f])+)\])$/,
                                message: 'Not valid email'
                            }
                        })}
                        name='email'
                        className='input__validation'
                        type='email'
                        placeholder='Email'
                    />
                    <div className='error'>{errors?.phone && <p className='not__valid__input'>{errors?.phone?.message}</p>}</div>
                    <input
                        {...register('phone', {
                            required: 'The string must not be empty',
                            pattern: {
                                value: /^[\+]{0,1}380([0-9]{9})$/,
                                message: 'Not valid phone'
                            }
                        })}
                        className='input__validation'
                        type='phone'
                        placeholder='Phone'
                    />
                    <p className='num__example'>+38 (XXX) XXX - XX - XX</p>
                    <p className='select__position'>Select your position</p>
                    {positions.map((position) => {
                    return    <div className='radio__item' key={position.id} >
                            <input  className='radio__btn' {...register("position_id")} type="radio" value={`${position.id}`}/>
                            <label className='radio__label'>{position.name}</label>
                        </div>
                    })}
                        <div className='error'>{errors?.photo && <p className='not__valid__input'>{errors?.photo?.message}</p>}</div>
                        <input
                            {...register('photo', {
                                required: 'Please add photo'
                            })}
                            className='input__load__file' type='file' name='photo'  accept=".jpg, .jpeg"/>
                    <div className='submit__btn'>
                        <button style={{marginBottom: '100px'}} className='custom__btn' type='submit'>Sing up</button>
                    </div>
                </form>
            </div>
        </section>
    );
};

export default Post;
