import './App.scss';
import Header from "./components/header/Header";
import Home from "./components/home-block/Home";
import Get from "./components/get-block/Get";
import Post from "./components/post-block/Post";
import {useEffect} from "react";

function App() {
    const sliceText = (str) => {
        if (str.length > 20) {
            return str.slice(0, 20) + '...'
        }
        return str
    }
    useEffect(async () => {
         await fetch('https://frontend-test-assignment-api.abz.agency/api/v1/token')
            .then(response => response.json())
            .then(data => localStorage.setItem('token',data.token))
    })

  return (
      <div className="wrapper">
        <Header/>
          <Home/>
          <Get sliceText={sliceText}/>
          <Post sliceText={sliceText}/>
      </div>
  );
}

export default App;
