import React, {useEffect, useState} from 'react';
import {CircularProgress, Tooltip} from '@mui/material';
import './../../App.scss'
import './Get.scss'
import MyButton from '../UI/MyButton';
import apiRequests from '../../api/apiRequests';

const Users = (props) => {

    let [showBtn, setShowBtn] = useState(true);

    const getUsers = async (pageNum) => {
        props.setLoader(true);
        await apiRequests.getUsers(pageNum)
            .then(data => {
                props.setUsers(props.users.concat(data.users));
                props.setPageNum((prev) => prev + 1);
                if (pageNum === data.total_pages) setShowBtn(false);
                props.setLoader(false);
            })
            .catch(e => console.log(e));
    }

    useEffect(() => {
        getUsers(props.pageNum)
    }, []);


    return (
        <section id='users' className='get__section'>
            <div className='get__container container'>
                <h1 className='get__title title'> Working with GET request</h1>
                <div className='users__cards'>
                    {props.users.map((user) => {
                        let name = props.sliceText(user.name)
                        let email = props.sliceText(user.email)
                        return <div key={user.id} className='user__card'>
                            <div className='body__card'>
                                <img className='avatar' src={user.photo}/>
                                <Tooltip title={user.name}>
                                    <div className='name'>{name}</div>
                                </Tooltip>
                                <div className='position'>{user.position}</div>
                                <Tooltip arrow title={user.email}>
                                    <div className='email'>{email}</div>
                                </Tooltip>
                                <div className='phone'>{user.phone}</div>
                            </div>
                        </div>
                    })}
                </div>
                {showBtn ? <div className='show-btn__container'>
                    <MyButton
                        onClick={() => getUsers(props.pageNum)}
                        disabled={props.loader}
                    >
                        Show more
                    </MyButton>
                    <span className='loader'>
                        {props.loader ? <CircularProgress sx={{width: '10px', height: '10px'}}/> : ''}
                    </span>
                </div> : null}
            </div>
        </section>
    );
};

export default Users;
