import React, {useEffect, useState} from 'react';
import './../../App.scss'
import './Get.scss'
import MyButton from "../UI/MyButton";

const Get = () => {

    let [pageNum, setPageNum] = useState(1)
    let [users, setUsers] = useState([])
    let [showBtn, setShowBtn] = useState(true)

    const getUsers = async (pageNum) => {
        await fetch(`https://frontend-test-assignment-api.abz.agency/api/v1/users?page=${pageNum}&count=6`)
           .then((response) => response.json())
           .then(data => {
               setUsers(users.concat(data.users))
               setPageNum((prev) => prev + 1)
               if (pageNum === data.total_pages) setShowBtn(false)
           })
           .catch(e => console.log(e))
    }

    useEffect( () =>  {
        getUsers(pageNum)
    }, [])

    return (
        <section className='get__section'>
            <div className='get__container container'>
            <h1 className='get__title title'> Working with GET request</h1>
            <div className='users__cards'>
                {users.map((user) => {
                    return <div key={user.id} className='user__card'>
                        <div className='body__card'>
                          <img className='avatar' src={user.photo}/>
                            <div className='name'>{user.name}</div>
                            <div className='position'>{user.position}</div>
                            <div className='email'>{user.email}</div>
                            <div className='phone'>{user.phone}</div>
                        </div>
                    </div>
                })}
            </div>
            <MyButton
                style={{marginBottom: '140px', width: '120px'}}
                onClick={() => getUsers(pageNum)}
            >
                Show more
            </MyButton>
            </div>
        </section>
    );
};

export default Get;
