import React, {useEffect, useRef, useState} from 'react';
import './../../App.scss'
import './Post.scss'
import {useForm, Controller} from "react-hook-form";
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import {FormControlLabel, Radio, RadioGroup} from "@mui/material";
import MyButton from "../UI/MyButton";




const Post = () => {

    let [positions, setPosition] = useState([])
    let hiddenFileInput = useRef(null)
    let nameFile = hiddenFileInput?.current?.files[0]?.name;

    useEffect( () => {
        fetch('https://frontend-test-assignment-api.abz.agency/api/v1/positions')
            .then(response => response.json())
            .then(data => setPosition(data.positions))
    }, [])

    const {control, formState: {errors, isValid}, reset, handleSubmit} = useForm({mode: 'all'})



    const setUser = async (data) => {
        console.log('qwe')
        // console.log(hiddenFileInput.current.files[0].name)
        console.log(data)
        // const formData = new FormData()
        // formData.append('name', data.name)
        // formData.append('phone', data.phone)
        // formData.append('position_id', data.position_id)
        // formData.append('email', data.email)
        // formData.append('photo', data.photo[0])
        // await fetch(`https://frontend-test-assignment-api.abz.agency/api/v1/users`, {
        //     method: 'POST',
        //     body: formData,
        //     headers: {
        //         'Token': localStorage.getItem('token')
        //     },
        // })
        //     .then((response) => response)
        //     .then(data => console.log(data))
    }

    return (
        <section id='register' className='post__section'>
            <div className='container'>
                <h1 className='post__title title'>Working with POST request</h1>
                <form onSubmit={handleSubmit(setUser)} className='form_submit'>
                    <Box sx={{height: '104px'}}>
                    <Controller
                            name='name'
                            control={control}
                            rules={{
                                required: 'The string must not be empty',
                                minLength: {
                                    value: 2,
                                    message: 'MinLength 2 elements'
                                },
                                maxLength: {
                                    value: 60,
                                    message: 'MaxLength 60 elements'
                                }
                            }}
                            render={({field: {value, onChange}}) => (
                                <TextField
                                    fullWidth={true}
                                    value={value}
                                    onChange={onChange}
                                    placeholder='Your name'
                                    label="Name"
                                    helperText={errors?.name && errors?.name?.message}
                                />
                            )}
                        />
                    </Box>
                    <Box sx={{height: '104px'}}>
                        <Controller
                        name='email'
                        control={control}
                        rules={{
                            required: 'The string must not be empty',
                            minLength: {
                                value: 2,
                                message: 'MinLength 2 elements'
                            },
                            maxLength: {
                                value: 100,
                                message: 'MaxLength 100 elements'
                            },
                            pattern: {
                                value: /^(?:[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*|"(?:[\x01-\x08\x0b\x0c\x0e-\x1f\x21\x23-\x5b\x5d-\x7f]|\\[\x01-\x09\x0b\x0c\x0e-\x7f])*")@(?:(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?|\[(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?|[a-z0-9-]*[a-z0-9]:(?:[\x01-\x08\x0b\x0c\x0e-\x1f\x21-\x5a\x53-\x7f]|\\[\x01-\x09\x0b\x0c\x0e-\x7f])+)\])$/,
                                message: 'Not valid email'
                            }
                        }}
                        render={({field: {value, onChange}}) => (
                            <TextField
                                fullWidth={true}
                                value={value}
                                onChange={onChange}
                                placeholder='Your email'
                                label="Email"
                                helperText={errors?.email && errors?.email?.message}
                            />
                        )}
                    />
                    </Box>
                        <Box sx={{height: '104px', position: 'relative'}}>
                    <Controller
                        name='phone'
                        control={control}
                        rules={{
                            required: 'The string must not be empty',
                            pattern: {
                                value: /^[\+]{0,1}380([0-9]{9})$/,
                                message: 'Not valid phone',

                            }
                        }}
                        render={({field: {value, onChange}}) => (
                            <TextField
                                fullWidth={true}
                                value={value}
                                onChange={onChange}
                                placeholder='Your phone'
                                label="Phone"
                                helperText={errors?.phone && errors?.phone?.message}
                            />
                        )}
                    />
                            <p className='num__example'>+38 (XXX) XXX - XX - XX</p>
                        </Box>
                    <Box sx={{marginBottom: '50px'}}>
                    <Controller
                        name='position_id'
                        control={control}
                        render={({field: {onChange, value}}) => (
                            <RadioGroup value={value} onChange={onChange}
                            >

                                {positions.map(position => (
                                    <FormControlLabel

                                        key={position.id}
                                        value={position.id}
                                        label={<p className='radio__label'>{position.name}</p>}
                                        control={<Radio/>}/>))}
                            </RadioGroup>
                        )}
                    />
                    </Box>
                    <Box sx={{marginBottom: '50px'}}>
                    <Controller
                        name='photo'
                        control={control}
                        rules={{
                            required: 'Please add photo'
                        }}
                        render={({field: {onChange, value}}) => (
                            <div className='upload-file__block'>
                                <button className='upload-btn' onClick={() => hiddenFileInput.current.click()}>
                                    Upload
                                </button>
                                <input
                                    name="photo"
                                    type='file'
                                    ref={hiddenFileInput}
                                    onChange={onChange}
                                    style={{display: 'none'}}
                                />
                                <div className='upload-file__name'><div style={{color: 'black'}}>{nameFile ? nameFile : 'UploadFile'}</div></div>
                            </div>

                        )}
                    />
                    </Box>
                <Box sx={{marginBottom: '100px'}}>
                    <MyButton disabled={!isValid} type='submit'>Submit</MyButton>
            </Box>
                </form>
            </div>
        </section>
    );
};

export default Post;
