import React from 'react';
import './../../App.scss'
import './Post.scss'
import {useForm} from "react-hook-form";
import MyButton from "../UI/MyButton/MyButton";

const Post = () => {
    const {
        register,
        formState: {
            errors,
            isValid
        },
        reset,
        handleSubmit
    } = useForm({
        mode: 'all'
    })

    const onSubmit = (data) => {
        console.log(JSON.stringify(data))
    }
    return (
        <section className='post__section'>
                <h1 className='post__title title'>Working with POST request</h1>
                <form className='post__form' onSubmit={handleSubmit(onSubmit)}>
                    <div className='error'>  {errors?.name && <p>{errors?.name?.message}</p>}</div>
                    <input
                        {...register('name', {
                            required: 'The string must not be empty',
                            minLength: {
                                value: 2,
                                message: 'MinLength 2 elements'
                            },
                            maxLength: {
                                value: 12,
                                message: 'MaxLength 12 elements'
                            },
                            pattern: {
                                value: /^([а-яёА-ЯË]{1,23}|[a-zA-Z]{1,12})$/,
                                message: 'Name must contain only liters'
                            }
                        })}
                        name='name'
                        className='input__validation'
                        type='name'
                        placeholder='Your name'
                    />
                    <div className='error'>  {errors?.email && <p className='not__valid__input'>{errors?.email?.message}</p>}</div>
                    <input
                        {...register('email', {
                            required: 'The string must not be empty',
                            pattern: {
                                value: /^([a-z0-9_-]+\.)*[a-z0-9_-]+@[a-z0-9_-]+(\.[a-z0-9_-]+)*\.[a-z]{2,6}$/,
                                message: 'Not valid email'
                            }
                        })}
                        name='email'
                        className='input__validation'
                        type='email'
                        placeholder='Email'
                    />
                    <div className='error'>Error</div>
                    <input className='input__validation' type='phone' placeholder='Phone'/>
                    <p className='num__example'>+38 (XXX) XXX - XX - XX</p>
                    <p className='select__position'>Select your position</p>
                    <div style={{alignItems:"center"}}>
                        <input className='radio__btn' {...register("radio")} type="radio" value="Frontend developer"/>
                        <label className='radio__label'>Frontend developer</label>
                    </div>
                    <div>
                        <input className='radio__btn'  {...register("radio")} type="radio" value="Backend developer"/>
                        <label className='radio__label'>Frontend developer</label>
                    </div>
                    <div>
                        <input className='radio__btn'  {...register("radio")} type="radio" value="Designer"/>
                        <label className='radio__label'>Designer</label>
                    </div>
                    <div>
                        <input className='radio__btn'  {...register("radio")} type="radio" value="QA"/>
                        <label className='radio__label'>QA</label>
                    </div>
                    <MyButton type='submit'>submit</MyButton>
                </form>
        </section>
    );
};

export default Post;
