import React, {useEffect, useRef, useState} from 'react';
import './../../App.scss'
import './Post.scss'
import {useForm, Controller} from "react-hook-form";
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import {FormControlLabel, Radio, RadioGroup, Tooltip} from "@mui/material";
import MyButton from "../UI/MyButton";


const Post = (props) => {

    let [positions, setPosition] = useState([]);
    let [activePosition, setActivePosition] = useState(1)

    let hiddenFileInput = useRef(null)
    let nameUploadFile = hiddenFileInput?.current?.files[0]?.name;
    let toggleUploadFile = nameUploadFile ? props.sliceText(nameUploadFile) : 'UploadFile'
    const {control, register, formState: {errors, isValid}, reset, handleSubmit} = useForm({mode: 'all'})
    let token = localStorage.getItem('token')


    useEffect(() => {
        fetch('https://frontend-test-assignment-api.abz.agency/api/v1/positions')
            .then(response => response.json())
            .then(data => setPosition(data.positions))
    }, [])
    const handleChange = event => {
        const fileUploaded = event.target.files[0];

        console.log(fileUploaded)
    };

    const setUser = async (data) => {
        console.log(data)
        const formData = new FormData()
        // console.log(hiddenFileInput.current.files)
        let position_id = Number.parseInt(data.position_id)
        formData.append('name', data.name)
        formData.append('phone', data.phone)
        formData.append('position_id', position_id)
        formData.append('email', data.email)
        // formData.append('photo', data.photo) ?????

        await fetch(`https://frontend-test-assignment-api.abz.agency/api/v1/users`, {
            method: 'POST',
            body: formData,
            headers: {
                'Token': token
            },
        })
            .then((response) => response)
            .then(data => console.log(data))
            .then(e => console.log(e))
    }

    return (
        <section id='register' className='post__section'>
            <div className='container'>
                <h1 className='post__title title'>Working with POST request</h1>
                <form onSubmit={handleSubmit(setUser)} className='form_submit'>
                    <Box sx={{height: '104px'}}>
                    <Controller
                            name='name'
                            control={control}
                            rules={{
                                required: 'The string must not be empty',
                                minLength: {
                                    value: 2,
                                    message: 'MinLength 2 elements'
                                },
                                maxLength: {
                                    value: 60,
                                    message: 'MaxLength 60 elements'
                                }
                            }}
                            render={({field: {value, onChange}}) => (
                                <TextField
                                    fullWidth={true}
                                    value={value}
                                    onChange={onChange}
                                    placeholder='Your name'
                                    label="Name"
                                    helperText={errors?.name && errors?.name?.message}
                                />
                            )}
                        />
                    </Box>
                    <Box sx={{height: '104px'}}>
                        <Controller
                        name='email'
                        control={control}
                        rules={{
                            required: 'The string must not be empty',
                            minLength: {
                                value: 2,
                                message: 'MinLength 2 elements'
                            },
                            maxLength: {
                                value: 100,
                                message: 'MaxLength 100 elements'
                            },
                            pattern: {
                                value: /^(?:[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*|"(?:[\x01-\x08\x0b\x0c\x0e-\x1f\x21\x23-\x5b\x5d-\x7f]|\\[\x01-\x09\x0b\x0c\x0e-\x7f])*")@(?:(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?|\[(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?|[a-z0-9-]*[a-z0-9]:(?:[\x01-\x08\x0b\x0c\x0e-\x1f\x21-\x5a\x53-\x7f]|\\[\x01-\x09\x0b\x0c\x0e-\x7f])+)\])$/,
                                message: 'Not valid email'
                            }
                        }}
                        render={({field: {value, onChange}}) => (
                            <TextField
                                fullWidth={true}
                                value={value}
                                onChange={onChange}
                                placeholder='Your email'
                                label="Email"
                                helperText={errors?.email && errors?.email?.message}
                            />
                        )}
                    />
                    </Box>
                        <Box sx={{height: '104px', position: 'relative'}}>
                    <Controller
                        name='phone'
                        control={control}
                        rules={{
                            required: 'The string must not be empty',
                            pattern: {
                                value: /^[\+]{0,1}380([0-9]{9})$/,
                                message: 'Not valid phone',

                            }
                        }}
                        render={({field: {value, onChange}}) => (
                            <TextField
                                fullWidth={true}
                                value={value}
                                onChange={onChange}
                                placeholder='Your phone'
                                label="Phone"
                                helperText={errors?.phone && errors?.phone?.message}
                            />
                        )}
                    />
                            <p className='num__example'>+38 (XXX) XXX - XX - XX</p>
                        </Box>
                    <Box sx={{marginBottom: '50px'}}>
                    <Controller
                        name='position_id'
                        control={control}
                        render={({field: {onChange, value}}) => (
                            <RadioGroup value={activePosition} onChange={(event) => setActivePosition(event.target.value)}
                            >

                                {positions.map(position => (
                                    <FormControlLabel
                                        key={position.id}
                                        value={position.id}
                                        label={<p className='radio__label'>{position.name}</p>}
                                        control={<Radio/>}/>))}
                            </RadioGroup>
                        )}
                    />
                    </Box>
                    <Box sx={{marginBottom: '50px'}}>
                        <div className='upload-file__block'>
                            <button className='upload-btn' onClick={() => hiddenFileInput.current.click()}>
                                Upload
                            </button>
                        </div>
                        <Tooltip arrow title={nameUploadFile}>
                            <div className='upload-file__name'>
                                <div style={{color: 'black'}}>{toggleUploadFile}</div>
                            </div>  
                        </Tooltip>
                        <input
                            {...register('photo', {
                                    // required: 'Please add photo'
                                })}
                                className='input__load__file'
                            ref={hiddenFileInput}
                            onChange={handleChange}
                            type='file'
                            name='photo'
                            accept=".jpg, .jpeg"
                            style={{display: 'none'}}
                        />
                        {/*<div className='upload-file__block'>*/}
                        {/*    <button className='upload-btn' onClick={ async () => hiddenFileInput.current.click()}>*/}
                        {/*        Upload*/}
                        {/*    </button>*/}
                        {/*    <input*/}
                        {/*        {...register('photo', {*/}
                        {/*            required: 'Please add photo'*/}
                        {/*        })}*/}
                        {/*        className='input__load__file'*/}
                        {/*        ref={hiddenFileInput}*/}
                        {/*        // style={{display: 'none'}}*/}

                        {/*        type='file' name='photo'  accept=".jpg, .jpeg"/>*/}
                        {/*    </div>*/}
                    {/*<Controller*/}
                    {/*    name='photo'*/}
                    {/*    control={control}*/}
                    {/*    rules={{*/}
                    {/*        required: 'Please add photo'*/}
                    {/*    }}*/}
                    {/*    render={({field: {onChange, value,ref}}) => (*/}
                    {/*        <div className='upload-file__block'>*/}
                    {/*            <button className='upload-btn' onClick={() => hiddenFileInput.current.click()}>*/}
                    {/*                Upload*/}
                    {/*            </button>*/}
                    {/*            <input*/}
                    {/*                */}
                    {/*                type='file'*/}
                    {/*                name='photo'*/}
                    {/*                ref={hiddenFileInput}*/}
                    {/*                value={value}*/}
                    {/*                onChange={handleChange}*/}
                    {/*                style={{display: 'none'}}*/}

                    {/*            />*/}
                    {/*            <Tooltip arrow title={nameUploadFile}>*/}
                    {/*            <div className='upload-file__name'><div style={{color: 'black'}}>{toggleUploadFile}</div></div>*/}
                    {/*            </Tooltip>*/}
                    {/*        </div>*/}
                    {/*    )}*/}
                    {/*/>*/}
                    </Box>
                <Box sx={{marginBottom: '100px'}}>
                    <MyButton disabled={!isValid} type='submit'>Submit</MyButton>
            </Box>
                </form>
            </div>
        </section>
    );
};

export default Post;
