import React, {useEffect, useState} from 'react';
import {CircularProgress} from '@mui/material';
import './../../App.scss';
import './users.scss';
import MyButton from '../UI/myButton';
import apiRequests from '../../api/apiRequests';
import User from "./user/user";
import UserProfile from "./openUser/userProfile";


const Users = (props) => {

    let [showBtn, setShowBtn] = useState(true);
    let [user, setUser] = useState({})
    let [userOpen, setUserOpen] = useState(false);

    const sliceText = (str) => {
        if (str.length > 20) return str.slice(0, 20) + '...';
        return str;
    };
    const getUsers = async (pageNum) => {
        props.setLoader(true);
        await apiRequests.getUsers(pageNum)
            .then(data => {
                props.setUsers(props.users.concat(data.users));
                props.setPageNum((prev) => prev + 1);
                if (pageNum === data.total_pages) setShowBtn(false);
                props.setLoader(false);
            })
            .catch(e => console.log(e));
    }

    const openUser = async (userId) => {
        try {
            const user = await apiRequests.getUser(userId)
            setUser(user.user)
            setUserOpen(true)
        } catch (e) {
            console.log(e)
        }
    }

    useEffect(() => {
        getUsers(props.pageNum)
            .catch(e => console.log(e));
    }, []);

    if (userOpen) {
        return <UserProfile/>
    }



    return (
        <section id='users' className='users__section'>
            <div className='users__container container'>
                <h1 className='users__title title'>Working with GET request</h1>
                <div className='users__cards'>
                    {props.users.map((user) => {
                        let name = sliceText(user.name);
                        let email = sliceText(user.email);
                        return <User openUser={openUser} key={user.id} user={user} name={name} email={email}/>
                    })}
                </div>
                {showBtn
                    ? <div className='show-btn__container'>
                        <MyButton onClick={() => getUsers(props.pageNum)} disabled={props.loader}>
                            Show more
                        </MyButton>

                        <span className='loader'>
                        {props.loader ? <CircularProgress sx={{width: '10px', height: '10px'}}/> : ''}
                    </span>
                    </div>
                    : null}
            </div>
        </section>
    );
};

export default Users;
